// PARSE REQUEST - CON CONFIG GLOBAL
try {
  const CONFIG = $('Load Config Global').item.json;
  const input = $('Webhook - Request Documents').item.json;

  const clientData = {
    name: String(input.name || input.client_name || '').trim() || 'Unknown',
    email: String(input.email || '').trim().toLowerCase(),
    phone: String(input.phone || '').trim(),
    preferred_language: String(input.preferred_language || input.language || CONFIG.DEFAULT_LANG).toLowerCase(),
    client_id: input.client_id || `CLI-${Date.now()}`,
    tax_year: parseInt(input.tax_year) || CONFIG.CURRENT_TAX_YEAR,
    service_type: String(input.service_type || 'PERSONAL').toUpperCase(),
    required_forms: Array.isArray(input.required_forms) ? input.required_forms : ['T1'],
    flags: Array.isArray(input.flags) ? input.flags : [],
    is_cross_border: Boolean(input.is_cross_border),
    has_foreign_income: Boolean(input.has_foreign_income),
    is_self_employed: Boolean(input.is_self_employed || (input.flags || []).includes('SELF_EMPLOYED')),
    has_rental: Boolean(input.has_rental || (input.flags || []).includes('RENTAL_INCOME')),
    has_investments: Boolean(input.has_investments || (input.flags || []).includes('INVESTMENTS')),
    has_crypto: Boolean(input.has_crypto || (input.flags || []).includes('CRYPTOCURRENCY')),
    is_corporate: Boolean(input.is_corporate || input.service_type === 'CORPORATE'),
    is_quebec: Boolean(input.is_quebec || input.bill96?.applies),
    upload_method: String(input.upload_method || 'google_drive').toLowerCase(),
    send_sms: input.send_sms !== false,
    send_email: input.send_email !== false,
    source: String(input.source || 'workflow'),
    received_at: new Date().toISOString(),
    _config: CONFIG,
    _error: null
  };

  return { json: clientData };
} catch (error) {
  const CONFIG = $('Load Config Global').item.json || {};
  return { json: { name: 'Error', client_id: `ERR-${Date.now()}`, _config: CONFIG, _error: error.message } };
}

// GENERATE DOCUMENT CHECKLIST - CON CONFIG GLOBAL
try {
  const client = $input.first().json;
  const CONFIG = client._config;

  const documentLibrary = {
    t4_slips: { id: 't4_slips', name_en: 'T4 Employment Income Slips', name_es: 'Comprobantes T4', name_fr: 'Feuillets T4', required: true, category: 'income' },
    t5_slips: { id: 't5_slips', name_en: 'T5 Investment Income Slips', name_es: 'Comprobantes T5', name_fr: 'Feuillets T5', required: false, category: 'income' },
    rrsp_receipts: { id: 'rrsp_receipts', name_en: 'RRSP Contribution Receipts', name_es: 'Recibos RRSP', name_fr: 'ReÃ§us REER', required: false, category: 'deductions' },
    medical_expenses: { id: 'medical_expenses', name_en: 'Medical Expense Receipts', name_es: 'Recibos MÃ©dicos', name_fr: 'ReÃ§us MÃ©dicaux', required: false, category: 'deductions' },
    charitable_donations: { id: 'charitable_donations', name_en: 'Charitable Donation Receipts', name_es: 'Recibos Donaciones', name_fr: 'ReÃ§us Dons', required: false, category: 'deductions' },
    id_documents: { id: 'id_documents', name_en: 'Photo ID + SIN', name_es: 'ID + SIN', name_fr: 'PiÃ¨ce ID + NAS', required: true, category: 'identification' },
    prior_year_return: { id: 'prior_year_return', name_en: 'Prior Year NOA', name_es: 'NOA AÃ±o Anterior', name_fr: 'ADC AnnÃ©e PrÃ©cÃ©dente', required: true, category: 'reference' },
    business_income: { id: 'business_income', name_en: 'Business Income Summary', name_es: 'Resumen Ingresos Negocio', name_fr: 'Sommaire Revenus Entreprise', required: true, category: 'business' },
    business_expenses: { id: 'business_expenses', name_en: 'Business Expense Receipts', name_es: 'Recibos Gastos Negocio', name_fr: 'ReÃ§us DÃ©penses Entreprise', required: true, category: 'business' },
    rental_income: { id: 'rental_income', name_en: 'Rental Income Records', name_es: 'Registros Alquiler', name_fr: 'Registres Location', required: true, category: 'rental' },
    rental_expenses: { id: 'rental_expenses', name_en: 'Rental Property Expenses', name_es: 'Gastos Propiedad Alquiler', name_fr: 'DÃ©penses PropriÃ©tÃ©', required: true, category: 'rental' },
    crypto_transactions: { id: 'crypto_transactions', name_en: 'Crypto Transaction History', name_es: 'Historial Crypto', name_fr: 'Historique Crypto', required: true, category: 'crypto' },
    foreign_income_proof: { id: 'foreign_income_proof', name_en: 'Foreign Income Documentation', name_es: 'Docs Ingresos Extranjeros', name_fr: 'Docs Revenus Ã‰trangers', required: true, category: 'foreign' },
    foreign_bank_accounts: { id: 'foreign_bank_accounts', name_en: 'Foreign Bank Statements', name_es: 'Estados Cuenta Extranjeros', name_fr: 'RelevÃ©s Bancaires Ã‰trangers', required: true, category: 'foreign' },
    financial_statements: { id: 'financial_statements', name_en: 'Financial Statements', name_es: 'Estados Financieros', name_fr: 'Ã‰tats Financiers', required: true, category: 'corporate' },
    releve_1: { id: 'releve_1', name_en: 'RelevÃ© 1 (Quebec)', name_es: 'RelevÃ© 1 (Quebec)', name_fr: 'RelevÃ© 1 (QuÃ©bec)', required: true, category: 'quebec' }
  };

  let requiredDocs = [];
  let optionalDocs = [];

  if (!client.is_corporate) {
    requiredDocs.push(documentLibrary.t4_slips, documentLibrary.prior_year_return);
    optionalDocs.push(documentLibrary.t5_slips, documentLibrary.rrsp_receipts, documentLibrary.medical_expenses, documentLibrary.charitable_donations);
    if (client.client_id.startsWith('CLI-') || client.client_id.startsWith('ERR-')) requiredDocs.push(documentLibrary.id_documents);
    if (client.is_self_employed) requiredDocs.push(documentLibrary.business_income, documentLibrary.business_expenses);
    if (client.has_rental) requiredDocs.push(documentLibrary.rental_income, documentLibrary.rental_expenses);
    if (client.has_crypto) requiredDocs.push(documentLibrary.crypto_transactions);
    if (client.is_cross_border || client.has_foreign_income) requiredDocs.push(documentLibrary.foreign_income_proof, documentLibrary.foreign_bank_accounts);
    if (client.is_quebec) requiredDocs.push(documentLibrary.releve_1);
  } else {
    requiredDocs = [documentLibrary.financial_statements, documentLibrary.prior_year_return];
  }

  const uniqueRequired = [...new Map(requiredDocs.map(d => [d.id, d])).values()];
  const uniqueOptional = [...new Map(optionalDocs.map(d => [d.id, d])).values()];

  return { json: { ...client, documents: { required: uniqueRequired, optional: uniqueOptional, total_required: uniqueRequired.length, total_optional: uniqueOptional.length, generated_at: new Date().toISOString() } } };
} catch (error) {
  const client = $input.first().json;
  return { json: { ...client, documents: { required: [], optional: [], total_required: 0, total_optional: 0, _error: error.message } } };
}

// GENERATE UPLOAD LINK - CON CONFIG GLOBAL
try {
  const client = $input.first().json;
  const CONFIG = client._config;

  const folderId = `${client.client_id}_${client.tax_year}_${Date.now()}`;
  const folderName = `${(client.name || 'Unknown').replace(/[^a-zA-Z0-9]/g, '_')}_${client.tax_year}`;

  const uploadLinks = {
    google_drive: { base_url: CONFIG.document_paths.UPLOAD_URL_TEMPLATE.replace('{FOLDER_ID}', CONFIG.document_paths.GOOGLE_DRIVE_CLIENTS || 'YOUR_FOLDER_ID'), instructions_en: 'Click the link and drag your files into the folder', instructions_es: 'Haga clic y arrastre sus archivos', instructions_fr: 'Cliquez et glissez vos fichiers' },
    dropbox: { base_url: `https://www.dropbox.com/request/${CONFIG.document_paths.DROPBOX_REQUEST_ID || 'YOUR_REQUEST_ID'}`, instructions_en: 'Click and upload your documents', instructions_es: 'Haga clic y suba sus documentos', instructions_fr: 'Cliquez et tÃ©lÃ©versez vos documents' },
    email: { base_url: 'mailto:documents@multicomm.ai', instructions_en: 'Email your documents as attachments', instructions_es: 'EnvÃ­e sus documentos por email', instructions_fr: 'Envoyez vos documents par courriel' }
  };

  const selectedMethod = uploadLinks[client.upload_method] || uploadLinks.google_drive;
  const lang = client.preferred_language || 'english';
  const langKey = `instructions_${lang === 'spanish' ? 'es' : lang === 'french' ? 'fr' : 'en'}`;

  return { json: { ...client, upload: { folder_id: folderId, folder_name: folderName, method: client.upload_method, url: selectedMethod.base_url, instructions: selectedMethod[langKey] || selectedMethod.instructions_en, expires_at: new Date(Date.now() + CONFIG.document_paths.LINK_EXPIRY_DAYS * 24 * 60 * 60 * 1000).toISOString() } } };
} catch (error) {
  const client = $input.first().json;
  return { json: { ...client, upload: { folder_id: `ERR-${Date.now()}`, method: 'email', url: 'mailto:documents@multicomm.ai', instructions: 'Email your documents', _error: error.message } } };
}

// GENERATE MESSAGES - CON CONFIG GLOBAL
try {
  const client = $input.first().json;
  const CONFIG = client._config;
  const lang = client.preferred_language || 'english';

  const firstName = (client.name || 'Client').split(' ')[0];
  const uploadUrl = client.upload?.url || 'N/A';
  const taxYear = client.tax_year || CONFIG.CURRENT_TAX_YEAR;

  const smsMessages = {
    english: `Hi ${firstName}! Your ${taxYear} tax docs are needed. Upload: ${uploadUrl} Questions? Reply here.`,
    spanish: `Hola ${firstName}! Necesitamos sus docs de impuestos ${taxYear}. Suba aquÃ­: ${uploadUrl} Preguntas? Responda.`,
    french: `Bonjour ${firstName}! Vos docs fiscaux ${taxYear} requis. TÃ©lÃ©versez: ${uploadUrl} Questions? RÃ©pondez.`
  };

  const emailSubjects = {
    english: `Action Required: Upload Your ${taxYear} Tax Documents`,
    spanish: `AcciÃ³n Requerida: Suba Sus Documentos de Impuestos ${taxYear}`,
