  const langSuffix = lang === 'spanish' ? 'es' : lang === 'french' ? 'fr' : 'en';
  const docList = requiredDocs.map(d => 'â€¢ ' + (d[`name_${langSuffix}`] || d.name_en || d.id)).join('\n');

  const emailBodies = {
    english: `Dear ${client.name},\n\nThank you for choosing us for your ${taxYear} tax preparation.\n\nðŸ“‹ REQUIRED DOCUMENTS:\n${docList}\n\nðŸ”— UPLOAD HERE:\n${uploadUrl}\n\n${client.upload?.instructions || ''}\n\nBest regards,\nMulticomm Tax Team`,
