  return { json: { ...client, messages: { sms: { to: client.phone || '', body: smsMessages[lang] || smsMessages.english, char_count: (smsMessages[lang] || smsMessages.english).length }, email: { to: client.email || '', subject: emailSubjects[lang] || emailSubjects.english, body: emailBodies[lang] || emailBodies.english } } } };
