  const client = $input.first().json;
  return { json: { ...client, messages: { sms: { to: '', body: 'Please check your email for tax document requirements.' }, email: { to: '', subject: 'Tax Documents Required', body: 'Please contact us.' }, _error: error.message } } };
}

// SMS Placeholder
const data = $input.first().json;
return { json: { ...data, sms_status: 'PENDING_SEND', sms_config: { to: data.phone || data.messages?.sms?.to || '', body: data.messages?.sms?.body || '', timestamp: new Date().toISOString() } } };

// PREPARE RESPONSE
try {
  const data = $input.first().json;
  const lang = data.preferred_language || 'english';

  return { json: {
    success: !data._error,
    client_id: data.client_id,
    client_name: data.name,
    tax_year: data.tax_year,
    documents: { required_count: data.documents?.total_required || 0, optional_count: data.documents?.total_optional || 0, checklist: (data.documents?.required || []).map(d => ({ id: d.id, name: d[`name_${lang === 'spanish' ? 'es' : lang === 'french' ? 'fr' : 'en'}`] || d.name_en, required: true })) },
    upload: { method: data.upload?.method || 'email', url: data.upload?.url || '', expires_at: data.upload?.expires_at || '' },
    notifications: { sms_sent: data.sms_status === 'PENDING_SEND', email_sent: data.email_status !== 'SKIPPED', sms_to: data.sms_status === 'PENDING_SEND' ? data.phone : null, email_to: data.email_status !== 'SKIPPED' ? data.email : null },
    message: `Document request sent to ${data.name || 'client'}. Upload link: ${data.upload?.url || 'N/A'}`,
    timestamp: new Date().toISOString()
  }};
} catch (error) {
  return { json: { success: false, message: 'Document request processed with errors.', error: error.message, timestamp: new Date().toISOString() } };
}
